import classes from "./CartItem.module.css";

const CartItem = (props) => {
  const price = `$${props.price.toFixed(2)}`;
  return (
    <li className={classes.cart - item}>
      <div>
        <h2>{props.name}</h2>
        <div>
          <span>{price}</span>
          <span>{props.amount}</span>
        </div>
      </div>
      <div>{props.amount}</div>
    </li>
  );
};

export default CartItem;
