import { useState } from "react";
import useInput from "../hook/use-input";

const SimpleInput = (props) => {
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailToched, setEnteredEmailToched] = useState(false);

  const {
    value: enteredName,
    hasError: nameInputHasError,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
  } = useInput();
  const enteredEmailIsValid = enteredEmail.includes("@");

  const emailInputInvalid = !enteredEmailIsValid && enteredEmailToched;

  let formIsValid = false;

  if (enteredNameIsValid && enteredEmailIsValid) {
    formIsValid(true);
  }

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const emailInputBlurHandler = (event) => {
    setEnteredEmailToched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    setEnteredNameToched(true);
    setEnteredEmailToched(true);
    if (!enteredNameIsValid || !enteredEmailIsValid) {
      return;
    }

    setEnteredName("");
    setEnteredEmail("");
    setEnteredNameToched(false);
    setEnteredEmailToched(false);
  };

  const nameInputClasses = nameInputInvalid
    ? "form-control invalid"
    : "form-control";

  const emailInputClasses = emailInputInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputInvalid && (
          <p className="error-text">Name must not be empty</p>
        )}
      </div>
      <div className={emailInputClasses}>
        <label htmlFor="email">Your E-mail</label>
        <input
          type="email"
          id="email"
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
        {emailInputInvalid && (
          <p className="error-text">Please enter a valid email.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
